int getKeyboardCode(int input)
{
   int output = 0;
   
   if (!input)
   return 0;
   
   if (input <= 0xFF)
   output = virtualKey[input];
   else
   if (input <= 0xFFFF)
   output = virtualKeyEx[input & 0xFF];
   
   if (!output)
   return input | 0x04000000;
   
   return output;
}
